var javaPackages = new JavaImporter(com.vordel.circuit, com.vordel.api.topology.model,
			com.vordel.trace, com.vordel.nm, com.vordel.api.topology.emt);
with (javaPackages) {

function invoke(msg)  {
    var callerSSLCert = msg.get(MessageProperties.HTTP_REQ_CLIENTCERT);

    if (!EmtTopology.isEmtEnabled()) {
        // Check that the caller is an Admin Node Manager
        var callerTopologyCertificate = new TopologyCertificate(callerSSLCert );
        if (!callerTopologyCertificate.isAdminNodeManager()) {
            Trace.error("This Node Manager has been called by a component that is not an Admin Node Manager");
            return false;
        }
    }

    // Check that the caller Admin Node Manager is in the same Domain
    if (!NodeManager.getInstance().getTopologyCertificate().matchingDomain(callerSSLCert )) {
        Trace.error("This Node Manager has been called by a component that is not in the same domain");
        return false;
    }

    return true;         
    }         
}

var javaPackages = new JavaImporter(com.vordel.circuit, com.vordel.trace);
with (javaPackages) {

function invoke(msg)         {    
    // By the time we hit here, we have done SSL authentication.
    // If we are a Basic Node Manager it will always be mutual SSL.
    // If we are an ANM it may be mutual, or server-side only.
    // The caller's SSL cert is in HTTP_REQ_CLIENTCERT.

    var sslCert= msg.get(MessageProperties.HTTP_REQ_CLIENTCERT);
    if (sslCert == null) {
        return false;
    }      

    return true;         
    }         
}

var javaPackages = new JavaImporter(com.vordel.nm, com.vordel.trace);
with (javaPackages) {

function invoke(msg)         {         
    var isAdmin = NodeManager.getInstance().getTopologyCertificate().isAdminNodeManager();
    if (!isAdmin) {
        Trace.error("This basic Node Manager has received a request, but no client-side SSL certificate was presented");
    }    
    return isAdmin;
    }
}

